export function keyValidator(value) {
    if (!isString(value) && !isFunction(value)) {
        throw new TypeError('Unsupported key type', value);
    }
    return value;
}
/**
 * @ignore
 * Checks whether the given parameter is a float number.
 * @param {*} value The value
 * @return {Number} value
 * @throws {TypeError} for an invalid parameter.
 */
export function numberValidator(value) {
    const numValue = parseFloat(value);
    if (!isFinite(numValue)) {
        throw new TypeError('a number is expected, but ' + value + ' [' + typeof value + '] given');
    }
    return numValue;
}
import {validateAgainstSetOfValues} from '../utils';

export function oneOfValueValidator(values, paramName) {
    return (value) => {
        const message = 'Supported ' + paramName + ' is expected (one of: ' + values + '), but ' + value +
            ' [' + typeof value + '] was given.';
        return validateAgainstSetOfValues(value, values, message);
    };
}
