/*
 * Class to convert points or arrays of points.
 *
 * By default, the points will be converted to {lon: 00, lat: 00} format.
 *
 * Points in the format [00.0, 00.0] are considered lon-lat points,
 * for lat-lon points set the option isLatLon to true;
 *
 * It is possible to convert the points to a custom format by passing a function in the options
 * as customPointConverter with params longitude and latitude - customPointConverter(longitude,latitude)
 */
export class PointFacade {

    constructor(options = {}) {
        this.options = options;
    }

    convert(data) {
        if (this._isSinglePoint(data)) {
            return this._convertPoint(data);
        }
        return this._convertToArrayOfPoints(data);
    }

    _isSinglePoint(data) {
        if (Array.isArray(data)) {
            if (data.length === 2 && typeof data[0] !== 'object') { // for [00, 00] points
                return true;
            }
            return false;
        }
        if (isString(data)) {
            const points = data.split(':');
            if (points.length >= 2) {
                return false;
            }
        }
        return true;
    }

    _convertToArrayOfPoints(data) {
        if (isString(data)) {
            return data.split(':').map(this._convertPoint.bind(this));
        }
        return data.map(this._convertPoint.bind(this));
    }

    _convertPoint(point) {

        // converts {lat: 00.0, lon: 00.0}
        if (has(point, 'lat') && has(point, 'lon')) {
            return this._covertToDefaultFormat(point.lon, point.lat);
        }

        // converts {latitude: 00.0, longitude: 00.0}
        if (has(point, 'latitude') && has(point, 'longitude')) {
            return this._covertToDefaultFormat(point.longitude, point.latitude);
        }

        // converts {x: 00.0, y: 00.0} x === longitude, y === latitude
        if (has(point, 'x') && has(point, 'y')) {
            return this._covertToDefaultFormat(point.x, point.y);
        }

        // converts [00.0, 00.0]
        if (Array.isArray(point) && point.length === 2) {
            if (this.options.isLatLon) {
                return this._covertToDefaultFormat(point[1], point[0]);
            }
            return this._covertToDefaultFormat(point[0], point[1]);
        }

        // converts "00.000,00.000"
        if (isString(point)) {
            return this._convertStringPoint(point);
        }

        return point;
    }

    _convertStringPoint(point) {
        const p = point.split(',');

        if (!isStringSinglePoint(point) || p.length !== 2) {
            throw new Error('The point is not valid: ' + point);
        }

        if (this.options.isLatLon) {
            return this._covertToDefaultFormat(p[1], p[0]);
        }
        return this._covertToDefaultFormat(p[0], p[1]);
    }

    _covertToDefaultFormat(longitude, latitude) {
        if ((!longitude && longitude !== 0) || (!latitude && latitude !== 0)) {
            throw new Error('Longitude and latitude must be provided.');
        }
        if (isFunction(this.options.customPointConverter)) {
            return this.options.customPointConverter(longitude, latitude);
        }
        return {
            lng: parseFloat(longitude),
            lat: parseFloat(latitude)
        };
    }
}
export const isStringSinglePoint = stringPoint => {
    if (/^-?\d+(\.\d+)?,-?\d+(\.\d+)?$/.test(stringPoint)) {
        return true;
    }
    return false;
};
import {PointFacade} from './pointFacade';
import isNil from 'lodash/isNil';
import isObject from 'lodash/isObject';

const pointFacade = new PointFacade();

export const convertResponsePoints = (elem) => {
    if (!elem) {
        return;
    }

    if (elem.boundingBox) {
        elem.boundingBox.btmRightPoint = pointFacade.convert(elem.boundingBox.btmRightPoint);
        elem.boundingBox.topLeftPoint = pointFacade.convert(elem.boundingBox.topLeftPoint);
    }
    if (elem.viewport) {
        elem.viewport.btmRightPoint = pointFacade.convert(elem.viewport.btmRightPoint);
        elem.viewport.topLeftPoint = pointFacade.convert(elem.viewport.topLeftPoint);
    }
    if (elem.position) {
        elem.position = pointFacade.convert(elem.position);
    }
    if (elem.summary && elem.summary.geoBias) {
        elem.summary.geoBias = pointFacade.convert(elem.summary.geoBias);
    }
};

export const setProperty = (properties, propertyName, value) => {
    if (!isNil(value)) {
        properties[propertyName] = value;
    }
};

export const newFeature = (properties, type) => {
    return {
        type: 'Feature',
        geometry: {type, coordinates: []},
        properties: properties
    };
};

export const newFeatureCollection = () => {
    return {
        type: 'FeatureCollection',
        features: []
    };
};

export const getFeature = (data) => {
    const properties = {};
    properties.id = data.id;

    if (isObject(data)) {
        Object.keys(data).forEach(name => {
            setProperty(properties, name, data[name]);
        });
    }

    const feature = newFeature(properties, 'Point');
    feature.geometry.coordinates = [data.position.lng, data.position.lat];

    return feature;
};

export const toGeoJson = (data) => {
    const result = newFeatureCollection();

    for (let i = 0; i < data.results.length; i++) {
        const feature = getFeature(data.results[i]);
        result.features.push(feature);
    }
    return result;
};
import { convertResponsePoints, toGeoJson } from '../modelUtils';

function convertEntryPoints(element) {
    if (element && element.entryPoints && Array.isArray(element.entryPoints)) {
        element.entryPoints.forEach(entryPoint => {
            convertResponsePoints(entryPoint);
        });
    }
}

export function searchModel(data) {
    if (!data) {
        return data;
    }
    // converts location points
    if (Array.isArray(data.results)) {
        data.results.forEach(result => {
            if (Array.isArray(result)) {
                result.forEach(r => {
                    convertResponsePoints(r);
                    convertEntryPoints(r);
                });
            } else {
                convertResponsePoints(result);
                convertEntryPoints(result);
            }
        });
    } else {
        convertResponsePoints(data);
        convertEntryPoints(data);
    }

    return Object.assign(data, {
        toGeoJson: () => toGeoJson(data)
    });
}
import {searchModel} from './search';

export function batchModel(data) {
    if (!Array.isArray(data.batchItems)) {
        return null;
    }

    data.batchItems = data.batchItems.map((batchItem) => {
        if (batchItem.response.error) {
            return { error: batchItem.response.error};
        } else {
            return searchModel(batchItem.response);
        }
    });

    return data;
}

