    (Object.prototype.hasOwnProperty.call(latlon, 'lon') || Object.prototype.hasOwnProperty.call(latlon, 'lng'));
}

function or(first, second) {
    if (first === undefined) {
        return second;
    }
    return first;
}

function throwTypeErrorWhen(condition, message) {
    if (condition) {
        throw new TypeError(message);
    }
}

function normalizeLatitude(latitude) {
    const numLatitude = parseFloat(latitude);
    if (!isFinite(numLatitude)) {
        throw new TypeError('an number is expected, but ' + latitude + ' [' + typeof latitude + '] given');
    }
    const i = Math.ceil((numLatitude - 90) / 180);
    return numLatitude - 180 * i;
}

function normalizeLongitude(longitude) {
    const numLongitude = parseFloat(longitude);
    if (!isFinite(numLongitude)) {
        throw new TypeError('an number is expected, but ' + longitude + ' [' + typeof longitude + '] given');
    }
    const i = Math.ceil((numLongitude - 180) / 360);
    return numLongitude - 360 * i;
}

function isXY(xy) {
    return Object.prototype.hasOwnProperty.call(xy, 'x') && Object.prototype.hasOwnProperty.call(xy, 'y');
}

function isLatitudeLongitude(value) {
    return Object.prototype.hasOwnProperty.call(value, 'latitude') &&
        Object.prototype.hasOwnProperty.call(value, 'longitude');
}

function isLatLonFunction(value) {
    return isFunction(value.lat) && isFunction(value.lng);
}

function castPoint(value) {
    if (Array.isArray(value) && value.length === 2) {
        return [...value].reverse();
    }
    if (isString(value)) {
        const match = value.match(pointRegex);
        throwTypeErrorWhen(!match || !match[1] || !match[2],
            'A point is expected, but ' + value + ' [' + typeof value + '] given');
        return [match[2], match[1]];
    }
    if (isLatLonFunction(value)) {
        return [value.lat(), value.lng()];
    }
    if (isLatLon(value)) {
        return [value.lat, or(value.lon, value.lng)];
    }
    if (isXY(value)) {
        return [value.y, value.x];
    }
    if (isLatitudeLongitude(value)) {
        return [value.latitude, value.longitude];
    }
    throw new TypeError('A point is expected, but ' + value + ' [' + typeof value + '] given');
}

function isLatLonBBox(bbox) {
    return Object.prototype.hasOwnProperty.call(bbox, 'minLon') &&
        Object.prototype.hasOwnProperty.call(bbox, 'minLat') &&
        Object.prototype.hasOwnProperty.call(bbox, 'maxLon') &&
        Object.prototype.hasOwnProperty.call(bbox, 'maxLat');
}

function isRightLeftTopBottomBBox(bbox) {
    return Object.prototype.hasOwnProperty.call(bbox, 'left') &&
        Object.prototype.hasOwnProperty.call(bbox, 'bottom') &&
        Object.prototype.hasOwnProperty.call(bbox, 'right') &&
        Object.prototype.hasOwnProperty.call(bbox, 'top');
}

function isWestEastNorthSouthBBox(bbox) {
    return isFunction(bbox.getWest) && isFunction(bbox.getEast) &&
        isFunction(bbox.getSouth) && isFunction(bbox.getNorth);
}

function castBoundingBox(bbox) {
    let sw, ne;
    if (isLatLonBBox(bbox)) {
        return bbox;
    }
    if (isRightLeftTopBottomBBox(bbox)) {
        return { minLon: bbox.left, minLat: bbox.bottom, maxLon: bbox.right, maxLat: bbox.top };
    }
    if (isWestEastNorthSouthBBox(bbox)) {
        return {
            minLon: bbox.getWest(),
            minLat: bbox.getSouth(),
            maxLon: bbox.getEast(),
            maxLat: bbox.getNorth()
        };
    }
    if (isFunction(bbox.getNorthEast) && isFunction(bbox.getSouthWest)) {
        ne = castPoint(bbox.getNorthEast());
        sw = castPoint(bbox.getSouthWest());
        return { minLon: sw[1], minLat: sw[0], maxLon: ne[1], maxLat: ne[0] };
    }
    if (Array.isArray(bbox) && bbox.length === 4) {
        return { minLon: bbox[0], minLat: bbox[1], maxLon: bbox[2], maxLat: bbox[3] };
    }
    if (Array.isArray(bbox) && bbox.length === 2) {
        sw = castPoint(bbox[0]);
        ne = castPoint(bbox[1]);
        return { minLon: sw[1], minLat: sw[0], maxLon: ne[1], maxLat: ne[0] };
    }
    if (isString(bbox)) {
        bbox = bbox.trim().split(/\s*,\s*/);
        if (bbox.length === 4) {
            return {
                minLon: parseFloat(bbox[0]),
                minLat: parseFloat(bbox[1]),
                maxLon: parseFloat(bbox[2]),
                maxLat: parseFloat(bbox[3])
            };
        }
    }
    throw new TypeError('Unable to cast ' + bbox + ' [' + typeof bbox + '] to bounding box');
}

function normalizeBoundingBox(bbox) {
    let w = bbox.minLon, e = bbox.maxLon, s = bbox.minLat, n = bbox.maxLat;
    if (e - w > 360) {
        e = 180;
        w = -180;
    } else {
        if (e > 270) {
            const right = Math.ceil(w / 360);
            e -= 360 * right;
            w -= 360 * right;
        }
        if (w < -270) {
            const left = Math.ceil(-e / 360);
            e += 360 * left;
            w += 360 * left;
        }
    }
    s = s < -90 ? -90 : s;
    n = n > 90 ? 90 : n;
    return { minLon: w, minLat: s, maxLon: e, maxLat: n };
}

function languageConverter(value, mapping) {

    if (isNil(value) || value === '') {
        return '';
    }

    if (mapping[value]) {
        return value;
    }

    value = value.toLowerCase();

    for (const language in mapping) {
        if (Object.prototype.hasOwnProperty.call(mapping, language) && language !== 'defaultValue' &&
            Object.prototype.hasOwnProperty.call(mapping[language].synonyms, value)
        ) {
            return language;
        }
    }
    console.warn('Value provided is invalid (' + value +
        '). Default value (' + mapping.defaultValue.value + ') will be used instead.');
    return mapping.defaultValue.value;
}

function geopoliticalViewConverter(value) {
    if (isNil(value) || value === '') {
        return '';
    }

    if (geopoliticalViewMappings[value]) {
        return value;
    }
    console.warn('Value provided is invalid (' + value +
        '). Default value (' + geopoliticalViewMappings.defaultValue + ') will be used instead.');
    return geopoliticalViewMappings.defaultValue;
}

function circleFromString(value) {
    const match = value.match(circleRegex);
    throwTypeErrorWhen(!match || !match[1] || !match[2] || !match[3],
        'Unable to cast ' + value + ' [' + typeof value + '] to circle');

    const lat = parseFloat(match[2]);
    const lon = parseFloat(match[1]);
    const radius = parseFloat(match[3]);
    throwTypeErrorWhen(!isFinite(lat) || !isFinite(lon),
        'Unable to cast ' + value + ' [' + typeof value + '] to circle');

    return 'circle(' + lat + ',' + lon + ',' + radius + ')';
}

function castAndNormalizePoint(value) {
    const point = castPoint(value);
    return normalizeLatitude(point[0]) + ',' + normalizeLongitude(point[1]);
}

function convertPointOrCircle(value) {
    if (isString(value) && value.indexOf('circle') > -1) {
        return circleFromString(value);
    }
    if (Array.isArray(value) && value.length === 3) {
        return 'circle(' + normalizeLatitude(value[1]) + ',' +
            normalizeLongitude(value[0]) + ',' + value[2] + ')';
    }
    return castAndNormalizePoint(value);
}

function extractPointsFromPolygon(inputPoints) {
    const points = [];
    for (const point in inputPoints) {
        if (Object.prototype.hasOwnProperty.call(inputPoints, point)) {
            points.push(castAndNormalizePoint(inputPoints[point]));
        }
    }
    return points;
}

function convertPointToLatitudeLongitude(point) {
    if (!isString(point)) {
        return point;
    }

    const splitPoint = point.split(',');

    return {
        latitude: splitPoint[0],
        longitude: splitPoint[1]
    };
}

function prepareSupportingPoints(supportingPoints) {
    return supportingPoints.map(convertPointToLatitudeLongitude);
}

export function convertBoundingBox(value) {
    return normalizeBoundingBox(castBoundingBox(value));
}

export function longitude(value) {
    return normalizeLongitude(value);
}

export function latitude(value) {
    return normalizeLatitude(value);
}

export function point(value) {
    return castAndNormalizePoint(value);
}

export function dateTime(value) {
    if (value && value instanceof Date) {
        return value.toISOString();
    }

    if (!value || !isString(value)) {
        throw new TypeError('Unable to cast ' + value + ' [' + typeof value + '] to datetime value.');
    }
    if (value !== 'now') {
        value = new Date(value).toISOString();
    }
    return value;
}

export function geometryList(value) {
    let figure;
    throwTypeErrorWhen(!value || !Array.isArray(value),
        'Unable to cast ' + value + ' [' + typeof value + '] to geometry list (array)');

    for (const figureName in value) {
        if (Object.prototype.hasOwnProperty.call(value, figureName)) {
            figure = value[figureName];
            if (figure.type === 'POLYGON') {
                figure.vertices = extractPointsFromPolygon(figure.vertices);
            } else if (figure.type === 'CIRCLE') {
                figure.position = castAndNormalizePoint(figure.position);
            }
        }
    }
    return value;
}

export function mapLanguage(value) {
    return languageConverter(value, mapsLanguageMap);
}

export function searchLanguage(value) {
    return languageConverter(value, searchLanguageMap);
}

export function routingGuidanceLanguage(value) {
    return languageConverter(value, routingLanguageMap);
}

export function incidentDetailsLanguage(value) {
    return languageConverter(value, trafficLanguageMap);
}

export function incidentDetailsV5Language(value) {
    return languageConverter(value, incidentV5LanguageMap);
}

export function geopoliticalView(value) {
    return geopoliticalViewConverter(value);
}

export function routingLocations(value) {
    const response = [];
    if (isString(value)) {
        return value
            .split(':')
            .map((point) => point.split(',').reverse().join(','))
            .join(':');
    }
    if (Array.isArray(value)) {
        throwTypeErrorWhen(value.length < 2,
            'Unable to cast ' + value + ' [' + typeof value + '] to routing locations string');

        // Convert first element to point
        response.push(castAndNormalizePoint(value[0]));

        // Convert elements from 1 to length-2 to point or circle
        for (let i = 1; i < value.length - 1; i += 1) {
            response.push(convertPointOrCircle(value[i]));
        }

        // Convert last element (length-1) to point
        response.push(castAndNormalizePoint(value[value.length - 1]));

        // Join converted elements
        return response.join(':');
    }
    throw new TypeError('Unable to cast ' + value + ' [' + typeof value + '] to routing locations string');
}

export function roadUse(value) {
    if (isString(value)) {
        return '["' + value.replace(/["']|^\s+|\s+$/g, '').split(/[\s,]+/).join('","') + '"]';
    }
    if (Array.isArray(value)) {
        for (let i = 0; i < value.length; i += 1) {
            value[i] = String(value[i]).replace(/["']|^\s+|\s+$/g, '');
        }
        return '["' + value.join('","') + '"]';
    }
    throw new TypeError('Unable to cast ' + value + ' [' + typeof value + '] to road use string');
}

export function arrayOf(options) {
    return (values) => {
        values.forEach((value) => {
            for (const key in value) {
                if (Object.prototype.hasOwnProperty.call(value, key) &&
                        Object.prototype.hasOwnProperty.call(options, key) &&
                        Array.isArray(options[key].converters)) {
                    options[key].converters.forEach((converter) => {
                        if (isFunction(converter)) {
                            value[key] = converter(value[key]);
                        }
                    });
                }
            }
        });
        return values;
    };
}

export function objectOf(options) {
    return (value) => {
        for (const key in value) {
            if (Object.prototype.hasOwnProperty.call(value, key) &&
                    Object.prototype.hasOwnProperty.call(options, key) &&
                    isFunction(options[key].converter)) {

                value[key] = options[key].converter(value[key]);
            }
        }
        return value;
    };
}

export function arrayOfStrings(value) {
    if (!value) {
        return [];
    }
    if (isString(value)) {
        value = value.trim().replace(/\s*[,;]\s*/g, ',');
        return value.split(/[,;]+/);
    }
    if (Array.isArray(value)) {
        return value;
    }
    throw new TypeError('Unable to cast ' + value + ' [' + typeof value + '] to array of strings');
}

export function route(values) {
    return {
        points: values.map(castPoint).map(([lat, lon]) => ({ lat, lon }))
    };
}

export function integer(value) {
    return Math.round(Number(value));
}

export function supportingPoints(value) {
    const arrayOfPoints = [];
    if (isString(value)) {
        value = value.split(':');
    }
    if (Array.isArray(value)) {

        // Convert elements to points
        for (let i = 0; i < value.length; i++) {
            arrayOfPoints.push(castAndNormalizePoint(value[i]));
        }
    } else {
        arrayOfPoints.push(castAndNormalizePoint(value));
    }
    return prepareSupportingPoints(arrayOfPoints);
}

export function constantSpeedConsumption(value) {
    if (isString(value)) {
        return value;
    }

    if (!Array.isArray(value)) {
        throw new TypeError('An array is required');
    }
    return value.join(':');
}

export function avoidAreas(value) {
    if (!Array.isArray(value)) {
        throw new TypeError('An array is required');
    }
    return {
        rectangles: value.map((area) => {
            return {
                southWestCorner: convertPointToLatitudeLongitude(castAndNormalizePoint(area.southWestCorner)),
                northEastCorner: convertPointToLatitudeLongitude(castAndNormalizePoint(area.northEastCorner))
            };
        })
    };
}

export function commaSeparated(value) {
    if (Array.isArray(value)) {
        return value.join();
    } else {
        throw new TypeError('An array is required');
    }
}

export function array(value) {
    return castArray(value);
}

export function removeWhitespaces(value) {
    if (typeof value === 'string') {
        return value.replace(/\s/g, '');
    } else {
        throw new TypeError('A string is required');
    }
}

export const SEARCH = 'search';
export const POI_SEARCH = 'poiSearch';
export const CATEGORY_SEARCH = 'categorySearch';
export const GEOMETRY_SEARCH = 'geometrySearch';
export const NEARBY_SEARCH = 'nearbySearch';
export const SEARCH_ALONG_ROUTE = 'searchAlongRoute';

/* eslint-disable max-len */
/**
 * Makes a search request using the TomTom
 * [Search API - Along Route Search](ALONG_ROUTE_SEARCH_URL).
 *
 * The Search Along Route endpoint allows you to perform a fuzzy search for
 * POIs along a specified route. This search is constrained by specifying the
 * Detour Time limiting measure.
 *
 * To send the route points, this service will use a POST request with the  {{#crossLink "Services.services.alongRouteSearch/route:parameter"}}{{/crossLink}} encoded as a JSON payload.
 * The minimum number of route points is 2.
 *
 * Parameters need to be passed to the constructor.
 *
 * ### Response
 * The response is extended with `getTrackingId()` method, which returns the `Tracking-ID`
 * associated with the request.
 *
 * Additionally, the response extends API response by providing `toGeoJson()` method, which converts along route search data into
 * FeatureCollection with <a target="_blank" rel=”noopener” href="https://tools.ietf.org/html/rfc7946#section-3.1.2">Point</a> geometry.
 *
 * Each point feature represents `poi` from the original response. Properties of `poi` are mapped into feature properties
 *
 * Please refer to {{#crossLinkModule "Services"}}Difference between
 * API responses and this library's responses{{/crossLinkModule}} section.
 *
 * @example
 *```js
 * tt.services.alongRouteSearch({
 *     key: <Your API key>,
 *     limit: 20,
 *     maxDetourTime: 120,
 *     query: 'gas station',
 *     route: [
 *         {
 *             'lat': 37.7524152343544,
 *             'lon':-122.43576049804686
 *         },
 *         {
 *             'lat': 37.70660472542312,
 *             'lon':-122.43301391601562
 *         },
 *         [-122.36434936523438, 37.712059855877314], // Another valid format
 *     ]
 *   }).then(function(response) {
 *     console.log('SUMMARY:');
 *     console.table(response.summary);
 *     console.log('RESULTS:');
 *     console.table(response.results);
 *   });
 * ```
 *
 * For a list of all available formats for routes, read the documentation for the
 * {{#crossLink "Services.services.alongRouteSearch/route:parameter"}}{{/crossLink}} option.
 *
 * @class alongRouteSearch
 * @namespace Services.services
 * @module Services
 * @uses KeyMixin
 * @uses QueryMixin
 * @uses BatchMixin
 * @uses BrandSetMixin
 * @uses OpeningHoursMixin
 * @uses CategorySetMixin
 * @uses ConnectorSetMixin
 * @uses TimeZoneMixin
 * @uses MapcodesMixin
 * @uses FuelSetMixin
 * @uses TrackingIdMixin
 * @uses MaxPowerKwMixin
 * @uses MinPowerKwMixin
 * @uses ViewMixin
 * @uses AbortSignalMixin
 * @constructor
 *
 * @param {Object} [options]
 * @param {Object} [additionalOptions] Additional options to be passed to the service.
 */
/* eslint-enable max-len */

import {SERVICE_TYPES} from 'Core/serviceTypes';
import {
    bool as validateBool, string as validateString, key as validateKey, trackingId as validateTrackingId,
    oneOfValue as validateOneOfValue, number as validateNumber, integerInInterval as validateIntegerInInterval,
    route as validateRoute, connectorSet as validateConnectorSet
} from '../validators';
import {route as convertRoute} from '../converters';
import { SEARCH_ALONG_ROUTE } from '../../common/searchTypes';
import parameterApplications from '../../common/parameterApplications';
import { v4 as uuid } from 'uuid';
import {modelResponse} from '../../model/modelResponse';
import {
    searchEndpoint,
    batchSearchQueryEndpoint,
    batchSyncSearchEndpoint,
    batchSearchEndpoint
} from 'endpoints';
import {searchModel} from '../../model/search/search';
import {
    singleRequestServiceFactory, batchRequestServiceFactory, serviceFactory
} from '../../core';

const searchType = SEARCH_ALONG_ROUTE;

const fields = {
    key: {
        validators: [validateKey]
    },

    trackingId: {
        validators: [validateTrackingId],
        application: parameterApplications.HEADER,
        defaultValue: uuid
    },

    /**
     * The maximum allowed value is 3600.
     *
     * @attribute maxDetourTime
     * @param {Number} [options.maxDetourTime] New maximum detour time in seconds.
     */
    maxDetourTime: {
        validators: [validateIntegerInInterval(1, 3600)],
        required: true
    },

    /**
     * @attribute spreadingMode
     * @param {String} [options.spreadingMode] Enables the spreading of returned results evenly along the route.
     */
    spreadingMode: {
        validators: [validateOneOfValue(['auto'], 'spreading mode')]
    },

    /**
     * The following formats are supported:
     *
     * - **String[]** (longitude/latitude pairs)
     *   - `["4.8,52.3", "4.8,52.3"]`
     * - **Array[]** (longitude/latitude pairs)
     *   - `[ [4.8,52.3], [4.8,52.3] ]`
     * - **Object[]**
     *   - `{lon: 4.8, lat: 52.3}`
     *   - `{lng: 5.8, lat: 53.3}`
     *   - `{x: 53.3, y: 5.8}`
     *   - `{longitude: 5.8, latitude: 53.3}`
     *   - `{lng: Function, lat: Function}` The functions should return a numeric value.
     *
     * @example
     *```js
     * function callbackFn(response) {
     *   console.log(response);
     * }
     *
     * tomtom.alongRouteSearch({
     *   route:[
     *     {
     *         lat: 37.7524152343544,
     *         lon:-122.43576049804686
     *     },
     *     {
     *         lat: 37.70660472542312,
     *         lon:-122.43301391601562
     *     }
     *   ]
     * })
     *
     * .then(callbackFn);
     *```
     *
     * @attribute route
     * @param {Array} [options.route] Route representation.
     */
    route: {
        required: true,
        converters: [convertRoute],
        validators: [validateRoute],
        application: parameterApplications.POST
    },

    query: {
        required: true,
        validators: [validateString],
        application: parameterApplications.PATH
    },

    // A custom "limit" docblock is used because the max limit value differs from the one used on the
    // other services (100).
    /**
     * @attribute limit
     * @param {Number} [options.limit=10] The maximum number of elements in the response. The maximum number is 20.
     */
    limit: {
        validators: [validateIntegerInInterval(1, 20)]
    },

    type: {
        validators: [validateString],
        defaultValue: searchType,
        application: parameterApplications.PATH
    },

    brandSet: {
        validators: [validateString]
    },

    categorySet: {
        validators: [validateString]
    },

    connectorSet: {
        validators: [validateConnectorSet]
    },

    minPowerKW: {
        validators: [validateNumber]
    },

    maxPowerKW: {
        validators: [validateNumber]
    },

    openingHours: {
        validators: [validateOneOfValue(['nextSevenDays'], 'openingHours parameter')]
    },

    timeZone: {
        validators: [validateOneOfValue(['iana'], 'timeZone parameter')]
    },

    /**
     * @attribute sortBy
     * @param {String} [options.sortBy='detourTime']  Parameter which provides possibility to sort returned results.
     * Values: `detourTime`, `detourDistance`, `detourOffset`
     *
     * * Detour distance will be calculated as a difference between the original distance and the distance of a new
     * route with Point Of Interest location.
     * * `detourDistance` field will be present in every response regardless of sorting parameter.
     * * If detour offset is set to false `detourOffset=false` and sorting parameter is set to detourOffset
     * `sortBy=detourOffset` then detour offset will be calculated and results will be sorted as expected, but
     * detourOffset value will not be present in the response.
     */
    sortBy: {
        validators: [validateOneOfValue(['detourTime', 'detourOffset', 'detourDistance'], 'sortBy')]
    },

    /**
     * @attribute detourOffset
     * @param {Boolean} [options.detourOffset=false] Parameter which turns on calculation of the distance between the
     * start of the route and the starting point of the detour to a POI.
     *
     * * Detour offset is also calculated when `sortBy` parameter is set to `detourOffset`.
     * * Value is provided in a response only when `detourOffset` is set to `true`.
     */
    detourOffset: {
        validators: [validateBool]
    },

    view: {
        validators: [validateString]
    }
};

export function alongRouteSearch(options, additionalOptions) {
    const singleRequest = singleRequestServiceFactory(searchEndpoint);
    const batchRequest = batchRequestServiceFactory(100, {
        single: batchSearchQueryEndpoint,
        batchSync: batchSyncSearchEndpoint,
        batch: batchSearchEndpoint
    });

    const handleServiceCall = (data, abortSignal) => {
        return singleRequest(fields, data, abortSignal)
            .then(response => modelResponse(response, searchModel));
    };
    const handleBatchServiceCall = (data, abortSignal) => {
        return batchRequest(fields, data, abortSignal)
            .then(response => modelResponse(response, searchModel));
    };

    return serviceFactory(
        fields,
        SERVICE_TYPES.SEARCH,
        'alongRouteSearch',
        handleServiceCall,
        handleBatchServiceCall
    )(options, additionalOptions);
}
import isString from 'lodash/isString';
import isEmpty from 'lodash/isEmpty';
import {isValidValue} from './utils';

const engineType = {
    combustion: 'combustion',
    electric: 'electric',
    any: 'any'
};

function validateIfPointsGreaterOrEqual(speedsAndConsumptions) {
    const sortedKeys = Object.keys(speedsAndConsumptions).sort(function(x, y) {
        return parseFloat(x) > parseFloat(y);
    });
    const keysLength = sortedKeys.length;

    if (speedsAndConsumptions[sortedKeys[keysLength - 2]] > speedsAndConsumptions[sortedKeys[keysLength - 1]]) {
        throw new Error('Consumption for two highest speeds should be increasing');
    }
}

function validateFloat(val) {
    if (isNaN(parseFloat(val)) || !isFinite(val)) {
        throw new Error('A value parsable to float is expected, but ' + val + ' [' + typeof val + '] given');
    }
}

/**
 * @param {String[]} arr
 */
function consumptionPairsValidator(arr) {
    const speedsAndConsumptions = {};
    arr.forEach(function(element) {
        const pair = element.split(',');
        if (pair.length !== 2) {
            throw new Error('Invalid number of parameters in the pair around ' + element);
        }
        if (pair[0].trim().length === 0) {
            throw new Error('Speed value must not be empty.');
        }
        if (pair[1].trim().length === 0) {
            throw new Error('Consumption value must not be empty.');
        }
        validateFloat(pair[0]);
        validateFloat(pair[1]);
        if (Object.prototype.hasOwnProperty.call(speedsAndConsumptions, parseFloat(pair[0]))) {
            throw new Error('Duplicate speed: ' + pair[0]);
        } else {
            speedsAndConsumptions[parseFloat(pair[0])] = parseFloat(pair[1]);
        }
    });
    if (Object.keys(speedsAndConsumptions).length > 1) {
        validateIfPointsGreaterOrEqual(speedsAndConsumptions);
    }
}

function checkIfCorrectEngineType(obj, type) {
    if (obj.vehicleEngineType && obj.vehicleEngineType !== type && type !== engineType.any) {
        throw new Error('Expecting vehicleEngineType set to ' + type);
    }
}

function efficiencyParameterRequired(obj, paramName) {
    if (!(isValidValue(obj.accelerationEfficiency) &&
        isValidValue(obj.decelerationEfficiency) &&
        isValidValue(obj.uphillEfficiency) &&
        isValidValue(obj.downhillEfficiency))) {
        throw new Error('Efficiency parameters are required when using ' + paramName);
    }
}

function hasEfficiencyParameterSet(obj) {
    return obj.accelerationEfficiency ||
        obj.decelerationEfficiency ||
        obj.uphillEfficiency ||
        obj.downhillEfficiency;
}

function validateDependantParameters(obj, paramName1, paramName2) {
    if (!(obj[paramName1] && obj[paramName2])) {
        throw new Error('Missing dependant parameter. Expecting both defined: ' + paramName1 + ', ' + paramName2);
    }
}

function validateEfficiencyParameters(obj, paramName1, paramName2) {
    if (obj[paramName1] * obj[paramName2] > 1) {
        throw new Error('Product of ' + paramName1 + ' and ' + paramName2 + ' cannot exceed 1');
    }
}

/**
 * @ignore
 * Consumption Model cannot be used with travelMode values bicycle and pedestrian.
 * @param {Object} obj
 */
function validateTravelMode(obj) {
    if (obj.travelMode === 'bicycle' || obj.travelMode === 'pedestrian') {
        throw new Error('Consumption model parameters cannot be set if travelMode is set to bicycle or pedestrian');
    }
}

/**
 * @ignore
 * All parameters require constantSpeedConsumption* to be specified by the user. It is an
 * error to specify any other consumption model parameter (with the exception of vehicleWeight)
 * if constantSpeedConsumption* is not specified.
 * @param {Object} obj
 */
function hasConstantSpeedConsumption(obj) {
    if (!obj.constantSpeedConsumptionInLitersPerHundredkm && !obj.constantSpeedConsumptionInkWhPerHundredkm) {
        throw new Error('Consumption model cannot be used without setting constant speed consumption parameter');
    }
}

/**
 * @ignore
 * The list must contain between 1 and 25 points (inclusive), and may not contain
 * duplicate points for the same speed. If it only contains a single point, then the
 * consumption rate of that point is used without further processing.
 * Consumption specified for the highest speed must be greater than or equal to
 * that of the penultimate highest speed. This ensures that extrapolation does not
 * lead to negative consumption rates. Similarly, consumption values specified for the
 * two lowest speeds in the list cannot lead to a negative consumption rate for any lower speed.
 * The minimum and maximum values described here refer to the valid range for the consumption
 * values (expressed in l/100km).
 * @param {Number} engineType
 * @param {Object} obj
 * @return {Function} function
 */
export function constantSpeedConsumption(engineType) {
    return (value, obj) => {
        if (value === undefined || value === null) {
            return;
        }
        validateTravelMode(obj);
        checkIfCorrectEngineType(obj, engineType);
        if (!isString(value)) {
            throw new TypeError('Expecting a String like "15.2,12.2:8.0,9.0"');
        }
        const speedPairs = value.split(':');
        if (speedPairs.length < 1 || speedPairs.length > 25) {
            throw new Error('Incorrect amount of speed-consumption pairs provided. Expecting 1-25, but got ' +
                value.length);
        }
        consumptionPairsValidator(speedPairs);
    };
}

export function vehicleWeight(value, obj) {
    if (hasEfficiencyParameterSet(obj) && value === undefined) {
        throw new Error('vehicleWeight parameter must be set if any efficiency parameters is present');
    }
}

export function floatAndEngineType(engineType, paramName) {
    return function(value, obj) {
        if (!value) {
            return;
        }
        validateTravelMode(obj);
        hasConstantSpeedConsumption(obj);
        checkIfCorrectEngineType(obj, engineType);
        validateFloat(value);
        if (value < 0) {
            throw new Error(paramName + ': Expecting positive value');
        }
    };
}

export function fuelEnergyDensityInMJoulesPerLiter(value, obj) {
    if (!value) {
        return;
    }
    validateTravelMode(obj);
    validateFloat(value);
    hasConstantSpeedConsumption(obj);
    checkIfCorrectEngineType(obj, 'combustion');
    efficiencyParameterRequired(obj, 'fuelEnergyDensityInMJoulesPerLiter');
}

/**
 * @ignore
 * accelerationEfficiency and decelerationEfficiency must always be specified as a pair (i.e., both or none).
 * uphillEfficiency and downhillEfficiency must always be specified as a pair (i.e., both or none).
 * If accelerationEfficiency and decelerationEfficiency are specified, the
 * product of their values must not be greater than 1 (to prevent perpetual motion).
 * If uphillEfficiency and downhillEfficiency are specified, the product of their
 * values must not be greater than 1 (to prevent perpetual motion).
 * @param {Number} paramName1
 * @param {Number} paramName2
 * @return {undefined} undefined
 */
export function efficiencyParameter(paramName1, paramName2) {
    return function(value, obj) {
        if (!value) {
            return;
        }
        validateTravelMode(obj);
        hasConstantSpeedConsumption(obj);
        checkIfCorrectEngineType(obj, engineType.any);
        validateDependantParameters(obj, paramName1, paramName2);
        /**
         * @ignore
         * If *Efficiency parameters are specified by the user, then vehicleWeight must also be specified.
         */
        validateDependantParameters(obj, paramName1, 'vehicleWeight');
        /**
         * @ignore
         * When vehicleEngineType is combustion, fuelEnergyDensityInMJoulesPerLiter must be specified as well.
         */
        if (obj.vehicleEngineType === engineType.combustion) {
            validateDependantParameters(obj, paramName1, 'fuelEnergyDensityInMJoulesPerLiter');
        }
        validateEfficiencyParameters(obj, paramName1, paramName2);
        validateFloat(value);
    };
}

export function chargeParameter(paramName1, paramName2) {
    return function(value, obj) {
        if (!value) {
            return;
        }
        validateTravelMode(obj);
        hasConstantSpeedConsumption(obj);
        checkIfCorrectEngineType(obj, engineType.electric);
        validateDependantParameters(obj, paramName1, paramName2);
        validateFloat(value);
    };
}

export function budgetInRange(_, data) {
    let maxBudget;
    let currentBudgetProvided;
    const electricEngine = data.vehicleEngineType === 'electric';
    if (electricEngine) {
        maxBudget = data.currentChargeInkWh;
        currentBudgetProvided = data.energyBudgetInkWh;
        if (currentBudgetProvided > maxBudget) {
            throw new Error('Energy budget may not be greater than current energy.');
        }
    } else {
        maxBudget = data.currentFuelInLiters;
        currentBudgetProvided = data.fuelBudgetInLiters;
        if (currentBudgetProvided > maxBudget) {
            throw new Error('Fuel budget may not be greater than current fuel.');
        }
    }
    if (currentBudgetProvided < 0) {
        throw new Error('Budget may not be negative.');
    }
}

export function requiredBudget(_, data) {
    const liters = 'fuelBudgetInLiters' in data;
    const watts = 'energyBudgetInkWh' in data;
    const seconds = 'timeBudgetInSec' in data;
    const meters = 'distanceBudgetInMeters' in data;
    const numberOfSetParams = [liters, watts, seconds, meters].filter(budget => budget).length;
    if (numberOfSetParams === 0 || numberOfSetParams > 1) {
        throw new Error('Exactly one of fuelBudgetInLiters, energyBudgetInkWh, ' +
            'timeBudgetInSec and distanceBudgetInMeters must be set.');
    }
}

export function requiredWithSpecificEngineType(value, data, key) {
    const consumptionInLiters = 'constantSpeedConsumptionInLitersPerHundredkm' in data;
    const consumptionInWatts = 'constantSpeedConsumptionInkWhPerHundredkm' in data;
    const electricEngine = data.vehicleEngineType === 'electric';

    if (key === 'energyBudgetInkWh' && value) {
        if (!electricEngine) {
            throw new Error('Engine type should be "electric" when energyBudgetInkWh is set');
        }
        if (!consumptionInWatts) {
            throw new Error('Missing constant speed consumption for electric engine.');
        }
    } else if (key === 'fuelBudgetInLiters' && value) {
        if (electricEngine) {
            throw new Error('Engine type should be "combustion" or undefined when fuelBudgetInLiters is set');
        }
        if (!consumptionInLiters) {
            throw new Error('Missing constant speed consumption for combustion engine.');
        }
    }
}

export function notCommon(_, data) {
    const forbiddenAvoid = 'alreadyUsedRoads';
    const forbiddenModes = ['bicycle', 'pedestrian'];

    if ('avoid' in data && data.avoid.indexOf(forbiddenAvoid) > -1) {
        throw new Error(forbiddenAvoid + ' is not allowed value for avoid parameter ' +
            'in Calculate Reachable Route request.');
    }

    if ('travelMode' in data && forbiddenModes.indexOf(data.travelMode) > -1) {
        throw new Error(data.travelMode + ' is not allowed value for travelMode parameter ' +
            'in Calculate Reachable Route request.');
    }

    if ('arriveAt' in data) {
        throw new Error('arriveAt parameter is not allowed in Calculate Reachable Route request.');
    }
}

export function firstParamCannotBeUsedWithSecond(paramNameUsed, paramNameToAvoid) {
    return function(value, obj) {
        if (!value) {
            return;
        }
        if (Object.prototype.hasOwnProperty.call(obj, paramNameToAvoid) &&
            isValidValue(obj[paramNameToAvoid])) {
            throw new Error(paramNameUsed + ' parameter cannot be used in conjunction with ' +
                paramNameToAvoid);
        }
    };
}

export function requiresDependantParameter(parameterName, dependantParameter) {
    return function(value, obj) {
        if (!value) {
            return;
        }
        if (!Object.prototype.hasOwnProperty.call(obj, dependantParameter)) {
            throw new Error(dependantParameter + ' must be specified when using with ' + parameterName);
        }
    };
}

export function notRequiredWithCategoryNorBrandSet(_, data) {
    const isQueryAbsent = isEmpty(data.query);
    const isBrandSetAbsent = isEmpty(data.brandSet);
    const isCategorySetAbsent = isEmpty(data.categorySet);
    if (isQueryAbsent && isBrandSetAbsent && isCategorySetAbsent) {
        throw new Error('Empty query parameter is only allowed when used with brandSet or categorySet filters');
    }
}

export function consumptionRecuperationAltitudeParameter(_, data) {
    const recuperationInkWhPerkmAltitudeLoss = data.recuperationInkWhPerkmAltitudeLoss;
    const consumptionInkWhPerkmAltitudeGain = data.consumptionInkWhPerkmAltitudeGain;

    validateFloat(recuperationInkWhPerkmAltitudeLoss);
    validateFloat(consumptionInkWhPerkmAltitudeGain);

    if (consumptionInkWhPerkmAltitudeGain < recuperationInkWhPerkmAltitudeLoss) {
        throw new Error('consumptionInkWhPerkmAltitudeGain must be greater than ' +
        'recuperationInkWhPerkmAltitudeLoss');
    }

    if (consumptionInkWhPerkmAltitudeGain > 500) {
        throw new Error('recuperationInkWhPerkmAltitudeLoss and less than 500.0');
    }

    if (recuperationInkWhPerkmAltitudeLoss < 0) {
        throw new Error('recuperationInkWhPerkmAltitudeLoss must be greater than 0.0');
    }
}
import {
    validateBoundingBox,
    bool as validateBool, string as validateString, trackingId as validateTrackingId,
    oneOfValue as validateOneOfValue, naturalInteger as validateNaturalInteger,
    languageCode as validateLanguageCode, point as validatePoint, countrySet as validateCountrySet,
    number as validateNumber, mapcodes as validateMapcodes, limit as validateLimit,
    connectorSet as validateConnectorSet, fuelSet as validateFuelSet, offset as validateOffset
} from '../../services/validators';
import {
    convertBoundingBox, searchLanguage as convertLanguage, point as convertPoint
