import { SEARCH } from '../../common/searchTypes';
import parameterApplications from '../../common/parameterApplications';
import {notRequiredWithCategoryNorBrandSet} from '../postValidators';
import { v4 as uuid } from 'uuid';

export default {
    create: () => {
        return {
            __all: {
                validators: [notRequiredWithCategoryNorBrandSet]
            },
            key: {
                validators: [validateString]
            },
            trackingId: {
                validators: [validateTrackingId],
                application: parameterApplications.HEADER,
                defaultValue: uuid
            },
            query: {
                validators: [
                    validateString
                ],
                application: parameterApplications.PATH,
                defaultValue: ''
            },
            typeahead: {
                validators: [validateBool]
            },
            limit: {
                validators: [validateLimit]
            },
            offset: {
                validators: [validateOffset],
                name: 'ofs'
            },
            language: {
                validators: [validateLanguageCode],
                converters: [convertLanguage]
            },
            countrySet: {
                validators: [validateCountrySet]
            },
            radius: {
                validators: [validateNaturalInteger]
            },
            center: {
                converters: [convertPoint], validators: [validatePoint],
                cast: (center, requestOptions) => {
                    const coordinates = center.split(',');
                    requestOptions.lat = coordinates[0];
                    requestOptions.lon = coordinates[1];
                }
            },
            type: {
                validators: [validateString],
                defaultValue: SEARCH,
                application: parameterApplications.PATH
            },
            bestResult: {
                validators: [validateBool],
                cast: (bestResults, requestOptions) => {
                    if (!bestResults) {
                        return;
                    }
                    requestOptions.limit = 1;
                    requestOptions.ofs = 0;
                }
            },
            protocol: {
                validators: [validateOneOfValue(['http', 'https'], 'protocol')]
            },
            extendedPostalCodesFor: {
                validators: [validateString]
            },
            view: {
                validators: [validateOneOfValue(['IL', 'MA', 'IN', 'PK', 'Unified', 'RU', 'TR', 'AR', 'CN'], 'view')]
            },
            brandSet: {
                validators: [validateString]
            },
            categorySet: {
                validators: [validateString]
            },
            connectorSet: {
                validators: [validateConnectorSet]
            },
            minPowerKW: {
                validators: [validateNumber]
            },
            maxPowerKW: {
                validators: [validateNumber]
            },
            openingHours: {
                validators: [validateOneOfValue(['nextSevenDays'], 'openingHours parameter')]
            },
            timeZone: {
                validators: [validateOneOfValue(['iana'], 'timeZone parameter')]
            },
            mapcodes: {
                validators: [validateMapcodes]
            },
            fuelSet: {
                validators: [validateFuelSet]
            },
            relatedPois: {
                validators: [validateOneOfValue(['off', 'child', 'parent', 'all'], 'relatedPois')]
            }
        };
    }
};

export const boundingBox = () => ({
    validators: [validateBoundingBox],
    converters: [convertBoundingBox],
    cast: (boundingBox, requestOptions) => {
        requestOptions.topLeft = `${boundingBox.maxLat},${boundingBox.minLon}`;
        requestOptions.btmRight = `${boundingBox.minLat},${boundingBox.maxLon}`;
    }
});

/**
 * Base class for all the Search services.
 *
 * @private
 * @class Search
 * @module Services
 * @uses ProtocolMixin
 * @uses KeyMixin
 * @uses LimitMixin
 * @uses LanguageMixin
 * @uses BatchMixin
 * @uses TimeZoneMixin
 * @uses TrackingIdMixin
 * @uses RelatedPoisMixin
 *
 * @constructor
 * @param {Object} [options] Options to be passed to the search call,
 *     or an array of such options objects to perform a batch request.
 */
/* jshint nomen:false */
import {SERVICE_TYPES} from 'Core/serviceTypes';
import {clone, addFields} from '../utils';
import {searchModel} from '../../model/search/search';
import {batchModel} from '../../model/search/batchModel';
import searchParams from './searchParameters';
import {modelResponse} from '../../model/modelResponse';
import {
    searchEndpoint,
    batchSearchQueryEndpoint,
    batchSyncSearchEndpoint,
    batchSearchEndpoint
} from 'endpoints';
import {
    serviceFactory,
    singleRequestServiceFactory,
    batchRequestServiceFactory
} from '../../core';

export class Search {
    constructor(options, additionalOptions) {
        this.options = options;
        this.additionalOptions = additionalOptions;
        this.defaultFields = clone(searchParams.create());
        this.fields = {};
    }

    _getRestService() {
        return {
            search: singleRequestServiceFactory(searchEndpoint),
            batch: batchRequestServiceFactory(100, {
                single: batchSearchQueryEndpoint,
                batchSync: batchSyncSearchEndpoint,
                batch: batchSearchEndpoint
            })
        };
    }

    handleBatchServiceCall(data, abortSignal) {
        return this._getRestService().batch(this.fields, data, abortSignal)
            .then(response => modelResponse(response, batchModel));
    }

    handleServiceCall(options, abortSignal) {
        return this._getRestService().search(this.fields, options, abortSignal)
            .then(({ data, trackingId }) => {
                return modelResponse({
                    data: options.bestResult ? data.results[0] : data,
                    trackingId
                }, searchModel);
            });
    }

    construct(serviceName) {
        this.fields = addFields(this.fields, this.defaultFields);
        const service = serviceFactory(
            this.fields,
            SERVICE_TYPES.SEARCH,
            serviceName || 'search',
            this.handleServiceCall.bind(this),
            this.handleBatchServiceCall.bind(this)
        );

        return service(this.options, this.additionalOptions);
    }
}
