import {batchCalculateRouteModel} from '../../model/routing/batchCalculateRoute';
import {getRoutingParameters} from './routingParameters';
import {modelResponse} from '../../model/modelResponse';
import {
    routingEndpoint,
    batchSyncRoutingEndpoint,
    batchRoutingEndpoint,
    batchRoutingQueryEndpoint
} from 'endpoints';
import {
    singleRequestServiceFactory,
    batchRequestServiceFactory,
