export function calculateRoute(options, additionalOptions) {
    const fields = getRoutingParameters();
    const maxNoOfElementsInBatchRequest = 100;
    const singleRequest = singleRequestServiceFactory(routingEndpoint);
    const batchRequest = batchRequestServiceFactory(maxNoOfElementsInBatchRequest, {
        batchSync: batchSyncRoutingEndpoint,
        batch: batchRoutingEndpoint,
        single: batchRoutingQueryEndpoint
    });

    function handleServiceCall(data, abortSignal) {
        return singleRequest(fields, data, abortSignal)
            .then(response => modelResponse(response, calculateRouteModel));
    }
    function handleBatchServiceCall(data, abortSignal) {
        return batchRequest(fields, data, abortSignal)
            .then(response => modelResponse(response, batchCalculateRouteModel));
    }

    return serviceFactory(
        fields,
        SERVICE_TYPES.ROUTING,
        'calculateRoute',
        handleServiceCall,
        handleBatchServiceCall
    )(options, additionalOptions);
}
